C<pre><code>#include &lt;stdio.h&gt;

int main() {
    FILE *fp;
    fp = fopen("test.txt", "r"); // 打开文件test.txt,以读模式打开
    if (fp == NULL) {
        printf("打开文件失败\n");
        return -1;
    }
    // 读文件内容
