        printf("%d ",temp-&gt;data);
        temp = temp-&gt;next;
    }
    printf("\n");
}
</code></pre>

C<pre><code>void delete(int data){
    struct Node *temp = head;
    struct Node *prev = NULL;
    while(temp!= NULL &amp;&amp; temp-&gt;data!= data){
        prev = temp;
        temp = temp-&gt;next;
    }
    if(temp == NULL){
        printf("Node not found\n");
    }else{
        if(prev == NULL){
            head = temp-&gt;next;
        }else{
            prev-&gt;next = temp-&gt;next;
        }
        free(temp);
    }
}
</code></pre>

